import React, { useState, useEffect } from 'react';
import { AssetInfo } from '../types';
import { assetApi } from '../services/api';

interface AssetCardProps {
  symbol: string;
  onRemove?: () => void;
  onClick?: () => void;
  showRemoveButton?: boolean;
}

const AssetCard: React.FC<AssetCardProps> = ({
  symbol,
  onRemove,
  onClick,
  showRemoveButton = false
}) => {
  const [assetInfo, setAssetInfo] = useState<AssetInfo | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchAssetInfo = async () => {
      try {
        setLoading(true);
        const info = await assetApi.getAssetInfo(symbol);
        setAssetInfo(info);
        setError(null);
      } catch (err) {
        setError('Failed to load asset data');
        console.error('Error fetching asset info:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchAssetInfo();
  }, [symbol]);

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(value);
  };

  const formatMarketCap = (value: number) => {
    if (value >= 1e12) return `$${(value / 1e12).toFixed(2)}T`;
    if (value >= 1e9) return `$${(value / 1e9).toFixed(2)}B`;
    if (value >= 1e6) return `$${(value / 1e6).toFixed(2)}M`;
    return `$${value.toLocaleString()}`;
  };

  const getPriceChangeColor = (current: number, previous: number) => {
    if (current > previous) return 'text-success-600';
    if (current < previous) return 'text-danger-600';
    return 'text-gray-600';
  };

  const getPriceChangeIcon = (current: number, previous: number) => {
    if (current > previous) return '↗';
    if (current < previous) return '↘';
    return '→';
  };

  if (loading) {
    return (
      <div className="card p-6 animate-pulse">
        <div className="flex justify-between items-start mb-4">
          <div>
            <div className="h-6 bg-gray-200 rounded w-20 mb-2"></div>
            <div className="h-4 bg-gray-200 rounded w-32"></div>
          </div>
          {showRemoveButton && (
            <div className="h-8 w-8 bg-gray-200 rounded"></div>
          )}
        </div>
        <div className="space-y-2">
          <div className="h-8 bg-gray-200 rounded w-24"></div>
          <div className="h-4 bg-gray-200 rounded w-16"></div>
        </div>
      </div>
    );
  }

  if (error || !assetInfo) {
    return (
      <div className="card p-6 border-danger-200">
        <div className="flex justify-between items-start mb-4">
          <div>
            <h3 className="text-lg font-semibold text-gray-900">{symbol}</h3>
            <p className="text-sm text-danger-600">{error || 'Asset not found'}</p>
          </div>
          {showRemoveButton && onRemove && (
            <button
              onClick={onRemove}
              className="text-gray-400 hover:text-danger-600 transition-colors"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          )}
        </div>
      </div>
    );
  }

  const priceChange = assetInfo.current_price - assetInfo.previous_close;
  const priceChangePercent = (priceChange / assetInfo.previous_close) * 100;

  return (
    <div 
      className={`card p-6 transition-all duration-200 ${
        onClick ? 'cursor-pointer hover:shadow-lg hover:border-primary-300' : ''
      }`}
      onClick={onClick}
    >
      <div className="flex justify-between items-start mb-4">
        <div>
          <h3 className="text-lg font-semibold text-gray-900">{assetInfo.symbol}</h3>
          <p className="text-sm text-gray-600 truncate max-w-48">{assetInfo.name}</p>
        </div>
        {showRemoveButton && onRemove && (
          <button
            onClick={(e) => {
              e.stopPropagation();
              onRemove();
            }}
            className="text-gray-400 hover:text-danger-600 transition-colors"
          >
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        )}
      </div>

      <div className="space-y-3">
        <div>
          <div className="flex items-center space-x-2">
            <span className="text-2xl font-bold text-gray-900">
              {formatCurrency(assetInfo.current_price)}
            </span>
            <span className={`text-sm font-medium ${getPriceChangeColor(assetInfo.current_price, assetInfo.previous_close)}`}>
              {getPriceChangeIcon(assetInfo.current_price, assetInfo.previous_close)}
              {formatCurrency(Math.abs(priceChange))} ({Math.abs(priceChangePercent).toFixed(2)}%)
            </span>
          </div>
        </div>

        <div className="grid grid-cols-2 gap-4 text-sm">
          <div>
            <span className="text-gray-500">Market Cap</span>
            <p className="font-medium">{formatMarketCap(assetInfo.market_cap)}</p>
          </div>
          <div>
            <span className="text-gray-500">Volume</span>
            <p className="font-medium">{assetInfo.volume.toLocaleString()}</p>
          </div>
          <div>
            <span className="text-gray-500">P/E Ratio</span>
            <p className="font-medium">{assetInfo.pe_ratio ? assetInfo.pe_ratio.toFixed(2) : 'N/A'}</p>
          </div>
          <div>
            <span className="text-gray-500">Dividend Yield</span>
            <p className="font-medium">
              {assetInfo.dividend_yield ? `${(assetInfo.dividend_yield * 100).toFixed(2)}%` : 'N/A'}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AssetCard;
