import React from 'react';
import { TrendingUp, TrendingDown, DollarSign, BarChart3 } from 'lucide-react';
import { AssetInfo } from '../types';

interface AssetCardProps {
  asset: AssetInfo;
  onClick?: () => void;
  showChart?: boolean;
}

const AssetCard: React.FC<AssetCardProps> = ({ asset, onClick, showChart = false }) => {
  const isPositive = asset.change_percent >= 0;
  const changeColor = isPositive ? 'text-success-600' : 'text-danger-600';
  const bgColor = isPositive ? 'bg-success-50' : 'bg-danger-50';
  const TrendIcon = isPositive ? TrendingUp : TrendingDown;

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(price);
  };

  const formatPercentage = (percentage: number) => {
    const sign = percentage >= 0 ? '+' : '';
    return `${sign}${percentage.toFixed(2)}%`;
  };

  const formatVolume = (volume: number) => {
    if (volume >= 1e9) {
      return `${(volume / 1e9).toFixed(1)}B`;
    } else if (volume >= 1e6) {
      return `${(volume / 1e6).toFixed(1)}M`;
    } else if (volume >= 1e3) {
      return `${(volume / 1e3).toFixed(1)}K`;
    }
    return volume.toString();
  };

  return (
    <div 
      className={`card hover:shadow-card-hover transition-all duration-200 ${
        onClick ? 'cursor-pointer hover:scale-105' : ''
      }`}
      onClick={onClick}
    >
      <div className="card-body">
        {/* Header */}
        <div className="flex items-start justify-between mb-4">
          <div className="flex-1">
            <div className="flex items-center space-x-2">
              <h3 className="text-lg font-bold text-gray-900">{asset.symbol}</h3>
              <span className="badge-secondary">{asset.asset_type}</span>
            </div>
            <p className="text-sm text-gray-600 mt-1">{asset.name}</p>
          </div>
          {showChart && (
            <div className="flex items-center space-x-2">
              <BarChart3 className="h-5 w-5 text-gray-400" />
            </div>
          )}
        </div>

        {/* Price Information */}
        <div className="space-y-3">
          <div className="flex items-center justify-between">
            <div>
              <div className="flex items-center space-x-2">
                <DollarSign className="h-4 w-4 text-gray-400" />
                <span className="text-2xl font-bold text-gray-900">
                  {formatPrice(asset.current_price)}
                </span>
              </div>
              <p className="text-xs text-gray-500 mt-1">Preço Atual</p>
            </div>
            <div className={`flex items-center space-x-1 px-3 py-1 rounded-full ${bgColor}`}>
              <TrendIcon className={`h-4 w-4 ${changeColor}`} />
              <span className={`font-semibold ${changeColor}`}>
                {formatPercentage(asset.change_percent)}
              </span>
            </div>
          </div>

          {/* Additional Information */}
          <div className="grid grid-cols-2 gap-4 pt-3 border-t border-gray-100">
            <div>
              <p className="text-xs text-gray-500">Abertura</p>
              <p className="text-sm font-medium text-gray-900">
                {formatPrice(asset.open_price)}
              </p>
            </div>
            <div>
              <p className="text-xs text-gray-500">Máxima</p>
              <p className="text-sm font-medium text-gray-900">
                {formatPrice(asset.high_price)}
              </p>
            </div>
            <div>
              <p className="text-xs text-gray-500">Mínima</p>
              <p className="text-sm font-medium text-gray-900">
                {formatPrice(asset.low_price)}
              </p>
            </div>
            <div>
              <p className="text-xs text-gray-500">Volume</p>
              <p className="text-sm font-medium text-gray-900">
                {formatVolume(asset.volume)}
              </p>
            </div>
          </div>

          {/* Market Status */}
          <div className="flex items-center justify-between pt-3 border-t border-gray-100">
            <div className="flex items-center space-x-2">
              <div className={`w-2 h-2 rounded-full ${
                asset.market_status === 'open' ? 'bg-success-500' : 'bg-gray-400'
              }`}></div>
              <span className="text-xs text-gray-600">
                {asset.market_status === 'open' ? 'Mercado Aberto' : 'Mercado Fechado'}
              </span>
            </div>
            <span className="text-xs text-gray-500">
              {new Date(asset.last_updated).toLocaleTimeString('pt-BR', {
                hour: '2-digit',
                minute: '2-digit'
              })}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AssetCard;
