import React from 'react';
import { Sun, Moon } from 'lucide-react';
import { useTheme } from '../contexts/ThemeContext';

const ThemeToggle: React.FC = () => {
  const { theme, toggleTheme } = useTheme();
  
  console.log('ThemeToggle renderizado - tema atual:', theme);

  return (
    <div className="flex items-center">
      <button
        onClick={toggleTheme}
        className="p-3 rounded-lg bg-blue-500 hover:bg-blue-600 text-white transition-colors duration-200 border-2 border-blue-700 shadow-lg"
        title={theme === 'light' ? 'Ativar tema escuro' : 'Ativar tema claro'}
        style={{ minWidth: '50px', minHeight: '50px', fontSize: '12px' }}
      >
        {theme === 'light' ? (
          <div className="flex flex-col items-center">
            <Moon className="h-5 w-5 text-white mb-1" />
            <span className="text-xs">Escuro</span>
          </div>
        ) : (
          <div className="flex flex-col items-center">
            <Sun className="h-5 w-5 text-yellow-300 mb-1" />
            <span className="text-xs">Claro</span>
          </div>
        )}
      </button>
    </div>
  );
};

export default ThemeToggle;
