import React, { useEffect, useRef } from 'react';
import { useTheme } from '../contexts/ThemeContext';

interface InvestingCalendarWidgetProps {
  width?: string | number;
  height?: string | number;
  theme?: 'light' | 'dark';
  timeZone?: string;
  timeSpan?: 'today' | 'tomorrow' | 'thisWeek' | 'nextWeek';
  showCountries?: string[];
  importanceLevel?: 1 | 2 | 3; // 1 = low, 2 = medium, 3 = high
  className?: string;
}

const InvestingCalendarWidget: React.FC<InvestingCalendarWidgetProps> = ({
  width = '100%',
  height = 600,
  theme,
  timeZone = 'America/Sao_Paulo',
  timeSpan = 'thisWeek',
  showCountries = ['BR', 'US', 'EU', 'GB', 'CN', 'JP'],
  importanceLevel = 3,
  className = '',
}) => {
  const { theme: contextTheme } = useTheme();
  const currentTheme = theme || contextTheme;
  const containerRef = useRef<HTMLDivElement>(null);
  const widgetRef = useRef<any>(null);

  useEffect(() => {
    // Limpar widget anterior se existir
    if (containerRef.current) {
      containerRef.current.innerHTML = '';
    }

    // Fun√ß√£o para criar o widget
    const createWidget = () => {
      if (!containerRef.current) {
        console.warn('Container n√£o encontrado');
        return;
      }

      try {
        // Configura√ß√£o do widget da Investing.com
        const iframe = document.createElement('iframe');
        iframe.setAttribute('frameBorder', '0');
        iframe.setAttribute('allowTransparency', 'true');
        iframe.setAttribute('scrolling', 'no');
        iframe.setAttribute('width', typeof width === 'number' ? `${width}px` : width);
        iframe.setAttribute('height', typeof height === 'number' ? `${height}px` : height);

        // Configurar par√¢metros do widget
        const params = new URLSearchParams();
        params.append('colorTheme', currentTheme === 'dark' ? 'dark' : 'light');
        params.append('timeZone', timeZone);

        // Configurar per√≠odo de tempo
        switch (timeSpan) {
          case 'today':
            params.append('timeFilter', 'timeDay');
            break;
          case 'tomorrow':
            params.append('timeFilter', 'timeDay_tomorrow');
            break;
          case 'thisWeek':
            params.append('timeFilter', 'timeWeek');
            break;
          case 'nextWeek':
            params.append('timeFilter', 'timeWeek_next');
            break;
          default:
            params.append('timeFilter', 'timeWeek');
        }

        // Configurar pa√≠ses
        if (showCountries && showCountries.length > 0) {
          params.append('countries', showCountries.join(','));
        }

        // Configurar n√≠vel de import√¢ncia
        params.append('importance', importanceLevel.toString());

        // Configurar idioma
        params.append('locale', 'pt');

        // URL do widget
        iframe.setAttribute('src', `https://sslecal2.investing.com/?${params.toString()}`);

        // Adicionar iframe ao container
        containerRef.current.appendChild(iframe);

        // Salvar refer√™ncia
        widgetRef.current = iframe;
      } catch (error) {
        console.error('Erro ao criar widget do Calend√°rio Econ√¥mico:', error);
        if (containerRef.current) {
          containerRef.current.innerHTML = `
            <div style="display: flex; align-items: center; justify-content: center; height: 100%; background: #f8f9fa; border: 1px solid #dee2e6; border-radius: 4px;">
              <div style="text-align: center; color: #6c757d;">
                <div style="font-size: 24px; margin-bottom: 8px;">üìÖ</div>
                <div>Calend√°rio indispon√≠vel</div>
                <div style="font-size: 12px; margin-top: 4px;">Erro ao carregar widget da Investing.com</div>
              </div>
            </div>
          `;
        }
      }
    };

    // Criar widget
    createWidget();

    return () => {
      // Limpar ao desmontar
      if (containerRef.current) {
        containerRef.current.innerHTML = '';
      }
    };
  }, [width, height, currentTheme, timeZone, timeSpan, showCountries, importanceLevel]);

  return (
    <div className={`investing-calendar-widget-container ${className}`} style={{ position: 'relative' }}>
      <div
        ref={containerRef}
        className="investing-calendar-widget"
        style={{
          width: typeof width === 'number' ? `${width}px` : width,
          height: typeof height === 'number' ? `${height}px` : height,
          background: contextTheme === 'dark' ? '#1f2937' : '#fff',
          borderRadius: 8,
          minHeight: 400,
        }}
      />
      <div className="investing-widget-copyright">
        <a
          href="https://www.investing.com/economic-calendar/"
          rel="noopener nofollow"
          target="_blank"
          className="text-xs text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300"
        >
          Calend√°rio Econ√¥mico por Investing.com
        </a>
      </div>
    </div>
  );
};

export default InvestingCalendarWidget;
