import React, { useMemo } from 'react';
import { useTheme } from '../contexts/ThemeContext';

interface InvestingCalendarWidgetProps {
  width?: string | number;
  height?: string | number;
  theme?: 'light' | 'dark';
  timeZone?: string;
  timeSpan?: 'today' | 'tomorrow' | 'thisWeek' | 'nextWeek';
  showCountries?: string[];
  importanceLevel?: 1 | 2 | 3; // 1 = low, 2 = medium, 3 = high
  className?: string;
}

const InvestingCalendarWidget: React.FC<InvestingCalendarWidgetProps> = ({
  width = '100%',
  height = 600,
  theme,
  timeZone = 'America/Sao_Paulo',
  timeSpan = 'thisWeek',
  showCountries = ['BR', 'US', 'EU', 'GB', 'CN', 'JP'],
  importanceLevel = 3,
  className = '',
}) => {
  const { theme: contextTheme } = useTheme();
  const currentTheme = theme || contextTheme;

  // Construir a URL do widget
  const widgetUrl = useMemo(() => {
    let src = 'https://sslecal2.investing.com/?';

    // Parâmetros básicos
    src += 'timezone=' + encodeURIComponent(timeZone);
    src += '&colorTheme=' + (currentTheme === 'dark' ? 'dark' : 'light');

    // Configurar período de tempo
    let timeFilter = '';
    switch (timeSpan) {
      case 'today':
        timeFilter = 'timeDay';
        break;
      case 'tomorrow':
        timeFilter = 'timeDay_tomorrow';
        break;
      case 'thisWeek':
        timeFilter = 'timeWeek';
        break;
      case 'nextWeek':
        timeFilter = 'timeWeek_next';
        break;
      default:
        timeFilter = 'timeWeek';
    }
    src += '&timeFilter=' + timeFilter;

    // Configurar países
    if (showCountries && showCountries.length > 0) {
      src += '&countries=' + showCountries.join(',');
    }

    // Configurar nível de importância
    src += '&importance=' + importanceLevel.toString();

    // Configurar colunas e categorias
    src += '&columns=exc_flags,exc_currency,exc_importance,exc_actual,exc_forecast,exc_previous';
    src += '&category=_employment,_economicActivity,_inflation,_centralBanks,_confidenceIndex,_balance,_Bonds';

    // Configurar recursos adicionais
    src += '&features=datepicker,timezone,timeselector,filters';

    // Configurar idioma (12 = português)
    src += '&lang=12';

    return src;
  }, [currentTheme, timeZone, timeSpan, showCountries, importanceLevel]);

  return (
    <div className={`investing-calendar-widget-container ${className}`} style={{ position: 'relative' }}>
      <div
        className="investing-calendar-widget"
        style={{
          width: typeof width === 'number' ? `${width}px` : width,
          height: typeof height === 'number' ? `${height}px` : height,
          background: contextTheme === 'dark' ? '#1f2937' : '#fff',
          borderRadius: 8,
          minHeight: 400,
          overflow: 'hidden',
        }}
      >
        <iframe
          src={widgetUrl}
          frameBorder="0"
          allowTransparency={true}
          scrolling="no"
          width="100%"
          height="100%"
          title="Calendário Econômico Investing.com"
          sandbox="allow-same-origin allow-scripts allow-popups allow-forms"
          allow="autoplay; fullscreen; picture-in-picture"
        />
      </div>
      <div className="investing-widget-copyright">
        <a
          href="https://www.investing.com/economic-calendar/"
          rel="noopener nofollow"
          target="_blank"
          className="text-xs text-gray-500 dark:text-gray-400 hover:text-gray-700 dark:hover:text-gray-300"
        >
          Calendário Econômico por Investing.com
        </a>
      </div>
    </div>
  );
};

export default InvestingCalendarWidget;
