import React, { useEffect, useRef, memo } from 'react';
import { TradingViewConfig } from '../types';

interface TradingViewChartProps {
  symbol: string;
  width?: string | number;
  height?: string | number;
  interval?: string;
  theme?: 'light' | 'dark';
  style?: string;
  locale?: string;
  timezone?: string;
  toolbar_bg?: string;
  enable_publishing?: boolean;
  withdateranges?: boolean;
  hide_side_toolbar?: boolean;
  allow_symbol_change?: boolean;
  save_image?: boolean;
  className?: string;
  // Novas op√ß√µes avan√ßadas
  studies?: string[];
  drawings_access?: boolean;
  show_popup_button?: boolean;
  studies_overrides?: Record<string, any>;
  time_frames?: Array<{ text: string; resolution: string; }>;
  charts_storage_url?: string;
  charts_storage_api_version?: string;
  client_id?: string;
  user_id?: string;
  fullscreen?: boolean;
  autosize?: boolean;
  container_id?: string;
  hide_top_toolbar?: boolean;
  enabled_features?: string[];
  disabled_features?: string[];
}

const TradingViewChart: React.FC<TradingViewChartProps> = ({
  symbol,
  width = '100%',
  height = 400,
  interval = 'D',
  theme = 'light',
  style = '1',
  locale = 'pt_BR',
  timezone = 'America/Sao_Paulo',
  toolbar_bg = '#f1f3f6',
  enable_publishing = false,
  withdateranges = true,
  hide_side_toolbar = false,
  allow_symbol_change = true,
  save_image = false,
  className = '',
  // Novas op√ß√µes avan√ßadas
  studies = [],
  drawings_access = true,
  show_popup_button = true,
  studies_overrides = {},
  time_frames = [
    { text: '1D', resolution: '1' },
    { text: '1W', resolution: 'W' },
    { text: '1M', resolution: 'M' },
  ],
  charts_storage_url = 'https://saveload.tradingview.com',
  charts_storage_api_version = '1.1',
  client_id = 'investtracker.app',
  user_id = 'public_user',
  fullscreen = false,
  autosize = true,
  container_id,
  hide_top_toolbar = false,
  enabled_features = [],
  disabled_features = [],
}) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const scriptRef = useRef<HTMLScriptElement | null>(null);
  const widgetRef = useRef<any>(null);

  useEffect(() => {
    // Limpar widget anterior se existir
    if (widgetRef.current) {
      try {
        widgetRef.current.remove();
      } catch (error) {
        console.warn('Erro ao remover widget anterior:', error);
      }
    }

    // Limpar container
    if (containerRef.current) {
      containerRef.current.innerHTML = '';
    }

    // Fun√ß√£o para criar o widget
    const createWidget = () => {
      if (!containerRef.current || !window.TradingView) {
        console.warn('TradingView n√£o dispon√≠vel ou container n√£o encontrado');
        return;
      }

      try {
        // Aguardar um pouco para garantir que o DOM est√° pronto
        setTimeout(() => {
          if (!containerRef.current) return;
          
          // Configura√ß√µes avan√ßadas para o TradingView
          const config: TradingViewConfig & Record<string, any> = {
            autosize: autosize,
            symbol: symbol,
            interval: interval,
            timezone: timezone,
            theme: theme,
            style: style,
            locale: locale,
            toolbar_bg: toolbar_bg,
            enable_publishing: enable_publishing,
            withdateranges: withdateranges,
            hide_side_toolbar: hide_side_toolbar,
            allow_symbol_change: allow_symbol_change,
            save_image: save_image,
            container_id: containerRef.current.id,
            width: width,
            height: height,
            fullscreen: fullscreen,
            hide_top_toolbar: hide_top_toolbar,
            show_popup_button: show_popup_button,
            time_frames: time_frames,
            enabled_features: [
              'study_templates',
              'use_localstorage_for_settings',
              'chart_property_page_trading',
              'chart_property_page_scales',
              'chart_property_page_style',
              'chart_property_page_background',
              'chart_property_page_timezone_sessions',
              'chart_property_page_events',
              'create_volume_indicator_by_default',
              ...enabled_features
            ],
            disabled_features: disabled_features,
            charts_storage_url: charts_storage_url,
            charts_storage_api_version: charts_storage_api_version,
            client_id: client_id,
            user_id: user_id,
            studies_overrides: studies_overrides,
          };

          // Criar o widget
          widgetRef.current = new window.TradingView.widget(config);

          // Adicionar indicadores t√©cnicos se especificados
          if (studies.length > 0 && widgetRef.current) {
            // Aguardar o widget estar pronto antes de adicionar estudos
            try {
              if (typeof widgetRef.current.onChartReady === 'function') {
                widgetRef.current.onChartReady(() => {
                  try {
                    studies.forEach(study => {
                      if (widgetRef.current && widgetRef.current.chart) {
                        widgetRef.current.chart().createStudy(study);
                      }
                    });
                  } catch (studyError) {
                    console.warn('Erro ao adicionar indicadores:', studyError);
                  }
                });
              } else {
                console.warn('onChartReady n√£o est√° dispon√≠vel no widget');
              }
            } catch (chartError) {
              console.warn('Erro ao configurar indicadores:', chartError);
            }
          }
        }, 100);
      } catch (error) {
        console.error('Erro ao criar widget TradingView:', error);
        // Mostrar mensagem de erro no container
        if (containerRef.current) {
          containerRef.current.innerHTML = `
            <div style="display: flex; align-items: center; justify-content: center; height: 100%; background: #f8f9fa; border: 1px solid #dee2e6; border-radius: 4px;">
              <div style="text-align: center; color: #6c757d;">
                <div style="font-size: 24px; margin-bottom: 8px;">üìä</div>
                <div>Gr√°fico indispon√≠vel</div>
                <div style="font-size: 12px; margin-top: 4px;">Erro ao carregar TradingView</div>
              </div>
            </div>
          `;
        }
      }
    };

    // Verificar se o script do TradingView j√° foi carregado
    if (window.TradingView) {
      createWidget();
    } else {
      // Verificar se j√° existe um script sendo carregado
      const existingScript = document.querySelector('script[src="https://s3.tradingview.com/tv.js"]');
      
      if (existingScript) {
        // Script j√° existe, aguardar carregamento
        existingScript.addEventListener('load', createWidget);
      } else {
        // Carregar script do TradingView
        const script = document.createElement('script');
        script.src = 'https://s3.tradingview.com/tv.js';
        script.async = true;
        script.onload = createWidget;
        script.onerror = () => {
          console.error('Erro ao carregar script do TradingView');
          // Mostrar mensagem de erro
          if (containerRef.current) {
            containerRef.current.innerHTML = `
              <div style="display: flex; align-items: center; justify-content: center; height: 100%; background: #f8f9fa; border: 1px solid #dee2e6; border-radius: 4px;">
                <div style="text-align: center; color: #6c757d;">
                  <div style="font-size: 24px; margin-bottom: 8px;">‚ö†Ô∏è</div>
                  <div>Erro de conex√£o</div>
                  <div style="font-size: 12px; margin-top: 4px;">N√£o foi poss√≠vel carregar o TradingView</div>
                </div>
              </div>
            `;
          }
        };
        
        document.head.appendChild(script);
        scriptRef.current = script;
      }
    }

    // Cleanup
    return () => {
      if (widgetRef.current) {
        try {
          widgetRef.current.remove();
        } catch (error) {
          console.warn('Erro ao limpar widget:', error);
        }
      }
    };
  }, [symbol, interval, theme, style, width, height, studies, enabled_features, disabled_features]);

  // Gerar ID √∫nico para o container
  const containerId = container_id || `tradingview_${symbol.replace(/[^a-zA-Z0-9]/g, '_')}_${Date.now()}`;

  return (
    <div className={`tradingview-widget-container ${className}`}>
      <div
        ref={containerRef}
        id={containerId}
        className="tradingview-widget"
        style={{
          width: typeof width === 'number' ? `${width}px` : width,
          height: typeof height === 'number' ? `${height}px` : height,
        }}
      />
      <div className="tradingview-widget-copyright">
        <a
          href={`https://www.tradingview.com/symbols/${symbol}/`}
          rel="noopener nofollow"
          target="_blank"
          className="text-xs text-gray-500 hover:text-gray-700"
        >
          <span className="blue-text">{symbol}</span> por TradingView
        </a>
      </div>
    </div>
  );
};

// Componente simplificado para gr√°fico b√°sico
export const SimpleTradingViewChart: React.FC<{
  symbol: string;
  height?: number;
  theme?: 'light' | 'dark';
}> = ({ symbol, height = 300, theme = 'light' }) => {
  // Valida√ß√£o do s√≠mbolo
  if (!symbol || symbol.trim() === '') {
    return (
      <div className="flex items-center justify-center bg-gray-50 border border-gray-200 rounded-lg" style={{ height }}>
        <div className="text-center">
          <div className="text-4xl mb-4">üìä</div>
          <p className="text-gray-500">S√≠mbolo inv√°lido</p>
        </div>
      </div>
    );
  }

  // Converter s√≠mbolos brasileiros para formato TradingView
  const formatSymbolForTradingView = (inputSymbol: string): string => {
    const cleanSymbol = inputSymbol.trim().toUpperCase();
    
    // Mapeamento de s√≠mbolos para TradingView
    const symbolMap: { [key: string]: string } = {
