// Componente para gr√°fico avan√ßado
export const AdvancedTradingViewChart: React.FC<{
  symbol: string;
  height?: number;
  theme?: 'light' | 'dark';
  interval?: string;
  studies?: string[];
}> = ({ symbol, height = 500, theme = 'light', interval = 'D', studies = [] }) => {
  // Indicadores t√©cnicos comuns
  const defaultStudies = studies.length > 0 ? studies : [
    'Volume',
    'Moving Average Exponential@tv-basicstudies',
  ];

  // Configura√ß√µes avan√ßadas para o gr√°fico
  const studiesOverrides = {
    'volume.volume.color.0': 'rgba(255, 82, 82, 0.5)',
    'volume.volume.color.1': 'rgba(76, 175, 80, 0.5)',
    'volume.volume.transparency': 50,
    'volume.volume ma.color': '#FF9800',
    'volume.volume ma.transparency': 30,
    'volume.volume ma.linewidth': 2,
    'volume.show ma': true,
    'volume.options.showStudyArguments': true,
    'moving average exponential.length': 21,
    'moving average exponential.plottype': 'line',
  };

  return (
    <TradingViewChart
      symbol={symbol}
      height={height}
      theme={theme}
      interval={interval}
      hide_side_toolbar={false}
      withdateranges={true}
      allow_symbol_change={true}
      save_image={true}
      enable_publishing={false}
      studies={defaultStudies}
      studies_overrides={studiesOverrides}
      enabled_features={[
        'study_templates',
        'use_localstorage_for_settings',
        'create_volume_indicator_by_default',
        'header_widget',
        'left_toolbar',
        'control_bar',
        'timeframes_toolbar',
        'display_market_status',
        'legend_widget',
        'header_chart_type',
        'header_compare',
        'header_indicators',
        'header_settings',
        'header_screenshot',
        'header_fullscreen_button',
        'show_chart_property_page',
        'property_pages',
      ]}
      time_frames={[
        { text: '1D', resolution: '1' },
        { text: '1W', resolution: 'W' },
        { text: '1M', resolution: 'M' },
        { text: '3M', resolution: '3M' },
        { text: '6M', resolution: '6M' },
        { text: 'YTD', resolution: '12M' },
        { text: '1Y', resolution: '12M' },
        { text: '5Y', resolution: '60M' },
        { text: 'ALL', resolution: 'ALL' },
      ]}
    />
  );
};

